import React, { useEffect, useState } from 'react'

const Fetch = () => {
  const defaulJokeValue = 10;
  const [maara, setMaara] = useState(defaulJokeValue);
  const [faktat, setFaktat] = useState([]);
  const [jokeCounter, setCount] = useState(maara)
  const tempArray = [];
  const [etunimi, setEtunnimi] = useState("")
  const [sukunimi, setSukunimi] = useState("")
  const lataa_faktat = () => {

    let base_url = "http://api.icndb.com/"
    let lastPArt = `jokes/random/${maara}?firstName=${etunimi}&lastName=${sukunimi}&limitTo=[nerdy,explicit]${[nerdy]}`
    console.log(lastPArt)
    fetch(base_url + lastPArt)
      .then((resp) => {
        return resp.json();
      })
      .then((json) => {
        console.log(json.value)
        setFaktat(json.value);
      });
  };



  const validateValue = (e) => {
    if (e >= 1 && e <= 10) {
      setCount(jokeCounter => jokeCounter + parseInt(e))
      setMaara(e)

      console.log(jokeCounter)

    }
  }


  
return (
  <div>
    <select>
      <option>vitsit</option>
      <option>jutut</option>
    </select>
    {console.log(etunimi+" " + sukunimi)}
    <button on onClick={() => lataa_faktat()}>Fetch</button>
    <input type="number" name="quantity" value={maara} onChange={e => validateValue(e.target.value)} />
    <input type="text" placeholder="etunimi" value={etunimi} onChange={e => setEtunnimi(e.target.value)}  ></input>
    <input type="text" placeholder="sukunimi" value={sukunimi} onChange={e => setSukunimi(e.target.value)}  ></input>
  </div>

);
}

export default Fetch;
