import React, { useEffect, useState } from 'react'

const Fetch = () => {
  const defaulJokeValue = 10;
  const [maara, setMaara] = useState(defaulJokeValue);
  const [faktat, setFaktat] = useState([]);
  const [jokeCounter, setCount] = useState(maara)
  const tempArray = [];
  const getJokes = () =>
  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          
          setFaktat(json.value);
        });
    };

    
  }, [maara]);
  
  const validateValue = (e) =>{
    if(e>=1 && e<=10){
      setCount(jokeCounter => jokeCounter+parseInt(e))
      setMaara(e)
      
      console.log(jokeCounter)
      
    }
  }
  
    
    
    
  return (
    <div>
      <select>
        <option>vitsit</option>
        <option>jutut</option>
      </select>
      <input
        value={maara}
        onChange={(e) => validateValue(e.target.value)}
        type="number"
      />
      <button onClick={() => { setFaktat([]); setMaara(0);}}>Tyhjennä</button>
      
      <button onClick={()=> useEffect()}>Fetch</button>
      
      <div>
        <h3>Faktat</h3>
        
        <p>{JSON.stringify(faktat.keys, ["joke"], 1)}</p>
        
        {
        
          
          //console.log(faktat[0]["joke"])
        }
        {
          faktat.map((element, i)=> {
            //console.log(element["joke"])
          return <li key={i}>{i+1}{". "}{element["joke"]}{"\n"}</li>
          })
        }
        <p>Vitsejä on haettu kaikkiaan: {jokeCounter}</p>
      </div>
    </div>

  );
}

export default Fetch;
