import React, { useEffect, useState, useContext } from 'react'
import { StoreContext } from "./storeContext";
import './Fetch.css'

/**
 * toivottavasti muistin siistiä koodia ja jos en niin sori
 * kategoriat on tehty kovakoodauksella mikäli en muistanut/jaksanut hakea niitä
 * 
 */
const Fetch = () => {
  const defaultJokeValue = 10;
  const [maara, setMaara] = useState(defaultJokeValue);
  //const [vitsit, setvitsit] = useState([]);
  //const [jokeCounter, setCount] = useState(maara)
  const [valittukategoria, setValittukategoria] = useState("kaikki", "explicit", "nerdy")
  const [etunimi, setEtunnimi] = useState("")
  const [sukunimi, setSukunimi] = useState("")
  const [kategoriat, setKategoriat] = useState([])
  const [store, setStore] = useContext(StoreContext);
  useEffect(() => {
    //hae kategoriat jsonilla
    setKategoriat(["kaikki", "explicit", "nerdy"])
  }, []);

  const lataa_vitsit = () => {
    let base_url = "http://api.icndb.com/"
    let lastPArt = `jokes/random/${maara}?firstName=${etunimi}&lastName=${sukunimi}&escape=javascript`
    let limitcat = `&limitTo=[${valittukategoria}]`
    let finalUrl = base_url + lastPArt

    if (valittukategoria === "kaikki") {
      finalUrl = base_url + lastPArt
    }
    else {
      finalUrl = base_url + lastPArt + limitcat
    }
    fetch(finalUrl)
      .then((resp) => {
        return resp.json();
      })
      .then((json) => {
        //console.log(json.value)
        //setvitsit(json.value);
        setStore(json.value)
      });
  };

  const validateValue = (e) => {
    if (e >= 1 && e <= 10) {
      //setCount(jokeCounter => jokeCounter + parseInt(e))
      setMaara(e)
    }
  }
  const kat = (e) => {
    setValittukategoria(e)
  }
  const handleKeypress = e => {      //it triggers by pressing the enter key    
    if (e.keyCode === 13) {
      validateValue();

    }
  };
  return (
    <div className="jokeBox">
      
        <select className="sel" onChange={e => kat(e.target.value)}>{kategoriat.map((x, i) => <option className="sel" key={i}>{x}</option>)}</select>
        <input type="number" alt="maara" name="quantity" value={maara} onChange={e => validateValue(e.target.value)} />
        <input type="text" placeholder="etunimi" value={etunimi} onChange={e => setSukunimi(e.target.value)}  ></input>
        <input type="text" placeholder="sukunimi" value={sukunimi} onChange={e => setSukunimi(e.target.value)}  ></input>
        <button type="submit" onSubmit={validateValue()}>Fetch</button>
        <div>
          {
            //vitsit.map((element, i) => {
            //console.log(element["joke"])
            //return <li key={i}>{i + 1}{". "}{element["joke"]}{" ---------------ja kategory on testissä   "}{element["categories"]}{"\n"}</li>
          }
        </div>
      
    </div>
    
  );
}

export default Fetch;
