import React, { useEffect, useState } from 'react'

const Search = () => {

  const [maara, setMaara] = useState(1);
  const [faktat, setFaktat] = useState([]);

  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          
          setFaktat({ value:json.value });
        });
    };

    lataa_faktat();
  }, [maara]);

  const formattedTasks = [];

  const tasks = Object.values(faktat);

  tasks.forEach(task =>
    Object.entries(task).forEach(([avain, arvo]) =>
      formattedTasks.push({ id: avain, joke: arvo })
    )
  );
  return (
    <div>
      <input
        value={maara}
        onChange={(e) => setMaara(e.target.value)}
        type="number"
      />
      <button
        onClick={() => setFaktat([])}

      >x</button>
      <button
        onClick={() => setMaara(0)}

      >O</button>

      <div>
        <h3>Faktat</h3>
        <p>Faktoja haettu {maara}</p>
        <p>{JSON.stringify(faktat, ["joke"], 2)}</p>
        
        {console.log(faktat.value.map())}

        {
          Object.keys(faktat).map((i, j, c) => <h4 key={i}>-------     {j} </h4>)
        }
      </div>
    </div>

  );
}

export default Search;
