const StoreContext = createContext([{}, () => {}]);

const StoreProvider = ({ children }) => {
  const [state, setState] = useState({
    counter: 0,
    items: [{}],
  });

  return (
    <StoreContext.Provider value={[state, setState]}>
      {children}
    </StoreContext.Provider>
  );
};

export { StoreContext, StoreProvider };
