import React, { useEffect, useState } from 'react'

const Fetch = () => {
  const defaulJokeValue = 10;
  const [maara, setMaara] = useState(defaulJokeValue);
  const [faktat, setFaktat] = useState([]);
  const [jokeCounter, setCount] = useState(maara)
  const [valittukategoria, setValittukategoria] = useState("")
  const [etunimi, setEtunnimi] = useState("")
  const [sukunimi, setSukunimi] = useState("")
  const [kategoriat, setKategoriat] = useState([])
  
  useEffect(() => {
    //hae kategoriat jsonilla
    
    setKategoriat(["kaikki", "explicit", "nerdy"])
    
  }, []);

  const lataa_faktat = () => {
    
    let base_url = "http://api.icndb.com/"
    let lastPArt = `jokes/random/${maara}?firstName=${etunimi}&lastName=${sukunimi}`
    console.log("VAlittu kategoria----- "+valittukategoria)
    let limitcat = `&limitTo=[${valittukategoria}]`
    let id = `jokes/23`
    let finalUrl = base_url+lastPArt
    
    if(valittukategoria == "kaikki"){
      finalUrl = base_url+lastPArt
    }
    else{
      finalUrl=base_url+lastPArt+limitcat
    }
    console.log("Haku urli" +finalUrl)
    fetch(finalUrl)
      .then((resp) => {
        return resp.json();
      })
      .then((json) => {
        //console.log(json)
        setFaktat(json.value);

      });
  };



  const validateValue = (e) => {
    if (e >= 1 && e <= 10) {
      setCount(jokeCounter => jokeCounter + parseInt(e))
      setMaara(e)

      //console.log(jokeCounter)

    }
  }
  const kat = (e) => {
    
    console.log("valittukategoria ursliin   -----" + e)
    setValittukategoria(e)
  }

  return (
    <div style={{ padding: 20, margin: 20 }}>

      <select style={{ padding: 10, margin: 20 }} onChange={e => kat(e.target.value)}>{kategoriat.map((x) => <option>{x}</option>)}</select>;

      {
        //<select onChange={e=> kat(e.target.value)}>{kategoriat.map((x) => <option>{x}</option>)}</select>;
        //console.log(etunimi+" " + sukunimi)
      }
      {
        //console.log("kategoriat"+kategoriat)
      }
      
      <input style={{ padding: 10, margin: 20 }} type="number" name="quantity" value={maara} onChange={e => validateValue(e.target.value)} />
      <input style={{ padding: 10, margin: 20 }} type="text" placeholder="etunimi" value={etunimi} onChange={e => setEtunnimi(e.target.value)}  ></input>
      <input style={{ padding: 10, margin: 20 }} type="text" placeholder="sukunimi" value={sukunimi} onChange={e => setSukunimi(e.target.value)}  ></input>
      <button style={{ padding: 10, margin: 20 }} onClick={() => lataa_faktat()}>Fetch</button>
    <p>{valittukategoria}</p>
      {
        console.log(faktat)
      }
       
       <div>
        <h3>Faktat</h3>
        
        <p>{JSON.stringify(faktat.keys, ["joke"], 1)}</p>
        
        {
        
          
          //console.log(faktat[0]["joke"])
        }
        {
          faktat.map((element, i)=> {
            //console.log(element["joke"])
          return <li key={i}>{i+1}{". "}{element["categories"]}{"\n"}</li>
          })
        }
        <p>Vitsej√§ on haettu kaikkiaan: {jokeCounter}</p>
      </div>
    </div>

  );
}

export default Fetch;
