import React, { useEffect, useState } from 'react'

const Search = () => {

  const [maara, setMaara] = useState(4);
  const [faktat, setFaktat] = useState([]);
  const [vitsit, setVitsit] = useState([])
  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          
          setFaktat(json.value);
        });
    };

    lataa_faktat();
  }, [maara]);
  
  
  
  return (
    <div>

      <input
        value={maara}
        onChange={(e) => setMaara(e.target.value)}
        type="number"
      />
      <button onClick={() => { setFaktat([]); setMaara(0);}}>Tyhjenn√§</button>
      
      
      
      <div>
        <h3>Faktat</h3>
        <p>Faktoja haettu {maara}</p>
        <p>{JSON.stringify(faktat.keys, ["joke"], 1)}</p>
        
        {
        
          
          //console.log(faktat[0]["joke"])
        }
        {
          faktat.map((element, i)=> {
            console.log(element["joke"])
          return <li key={i}>{i+1}{" "}{element["joke"]}{"\n"}}</li>
          })
        }
        
      </div>
    </div>

  );
}

export default Search;
