import React, { useEffect, useState } from 'react'

const Fetch = () => {
  const defaulJokeValue = 10;
  const [maara, setMaara] = useState(defaulJokeValue);
  const [faktat, setFaktat] = useState([]);
  const [jokeCounter, setCount] = useState(maara)
  const tempArray = [];
  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          
          setFaktat(json.value);
        });
    };

    lataa_faktat();
  }, [maara]);
  
  const validateValue = (e) =>{
    if(e>=1 && e<=10){
      setCount(jokeCounter => jokeCounter+parseInt(e))
      setMaara(e)
      
      console.log(jokeCounter)
      
    }
  }
  
  const input = props => {

    let textInput = React.createRef();
    
    function handleClick() {
      console.log(textInput.current.value);
    }
    
    return (
        <div>
          <input ref={textInput} placeholder="Type a message..." />
          <div onClick={handleClick} className="icon">
            <i className="fa fa-play" />
          </div>
        </div>
      )
    }
    
    
  return (
    <div>
      <select>
        <option>vitsit</option>
        <option>jutut</option>
      </select>
      <input
        value={maara}
        onChange={(e) => validateValue(e.target.value)}
        type="number"
      />
      <button onClick={() => { setFaktat([]); setMaara(0);}}>Tyhjenn√§</button>
      <button onClick>Fetch</button>
      
      
      <div>
        <h3>Faktat</h3>
        
        {
          faktat.map((element, i)=> {
            console.log(element["joke"])
          
          })
        }
        
      </div>
    </div>

  );
}

export default Fetch;
