import React, { useEffect, useState } from 'react'

const Search = () => {
  
  const [maara, setMaara] = useState(0);
  const [faktat, setFaktat] = useState([]);

  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"
      
      fetch(base_url + `jokes/15`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          console.log(json);
          setFaktat(json);
        });
    };

    lataa_faktat();
  }, [maara]);

  return (
    <div>
    <input
      value={maara}
      onChange={(e) => setMaara(e.target.value)}
      type="number"
    />

    <div>
      <h3>Faktat</h3>
      <p>Faktoja haettu {maara}</p>
      
      {console.log(faktat.value)}
    </div>
  </div>

  );
}

export default Search;
