import React, { useEffect, useState } from 'react'

const Search = () => {

  const [maara, setMaara] = useState(4);
  const [faktat, setFaktat] = useState([]);

  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {

          setFaktat(json.value);
        });
    };

    lataa_faktat();
  }, [maara]);
  let vitsitLista = [];
  let addVitsit = () => {
    for (let index = 0; index < maara; index++) {
      console.log(faktat[index]["joke"])
      vitsitLista.push(faktat[index]["joke"])
      
    }
  }

  return (
    <div>
      <input
        value={maara}
        onChange={(e) => setMaara(e.target.value)}
        type="number"
      />
      <button
        onClick={() => setFaktat([])}

      >x</button>
      <button
        onClick={() => setMaara(0)}

      >O</button>

      <div>
        <h3>Faktat</h3>
        <p>Faktoja haettu {maara}</p>
        <p>{JSON.stringify(faktat.keys, ["joke"], 2)}</p>
        {
          //console.log(faktat[0]["joke"])
        }
        {console.log(vitsitLista)}
        <p></p>
        {
          addVitsit()
          //console.log(Object.entries(faktat).forEach(([key, value]) => console.log(key)))
        }


      </div>
    </div>

  );
}

export default Search;
