import { StoreContext } from "./storeContext";
import React, { useEffect, useContext, useState, useRef } from "react";

/**
 * Ihan hirveetä koodia mut toimii, eka count päivitys on ehdollistettu
 * koska count value on alussa (1), jota en tietenkään halunnut
 * varmaan olis löytyny joku järkevämpikin tapa tehdä tää
 */
const PrintJokes = () => {
  const [store, setStore] = useContext();
  const [count, setCount] = useState(0);
  const j = Object.values(store);
  const [firstUpdate, setFirstUpdate] = useState(true)


  useEffect(() => {
    if (firstUpdate) {
      setFirstUpdate(false)
    }
    else {
      setCount((+j.length + +count))
    }
  }, [store])

  return (
    <div style={{ backgroundColor: "green",padding: 10, margin: 20 }}>
      <h3>vitsit</h3>
      {
        //hae kategoriat jsonilla
       
      }
      <p>pöö</p>
      {j.map((element,i) => {
         
         
        if(!firstUpdate&&count===0){
          return <h3>{"Ei yhtään vitsiä"}</h3>

        }
        
      return <p key={element.id}>{i+1}{". "}{element.joke}{"\n"}</p>
          
        
      })

      }

      <p>pöö</p>
      <h2>{count}</h2>

    </div>
  )
}
export default PrintJokes;
