import React, { useEffect, useState } from 'react'

const Search = () => {

  const [maara, setMaara] = useState(4);
  const [faktat, setFaktat] = useState([]);
  const [vitsit, setVitsit] = useState([])
  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          
          setFaktat(json.value);
        });
    };

    lataa_faktat();
  }, [maara]);
  
  const tulosta = () =>{
    faktat.map((element, i)=> {
      console.log(element["joke"])
      setVitsit([...vitsit, element["joke"]])
    });
  }
  useEffect(() => {
    faktat.forEach(item => changeState(prevState => ({...prevState, [item]: "" })));
  }, [props.input])
  
  return (
    <div>

      <input
        value={maara}
        onChange={(e) => setMaara(e.target.value)}
        type="number"
      />
      <button
        onClick={() => setVitsit([])}

      >x</button>
      <button
        onClick={() => setMaara(0)}

      >O</button>
      <button onClick={()=>tulosta()}>tulosta</button>
      
      <div>
        <h3>Faktat</h3>
        <p>Faktoja haettu {maara}</p>
        <p>{JSON.stringify(faktat.keys, ["joke"], 2)}</p>
        
        {
        
          
