import React, { useEffect, useState } from 'react'

const Search = () => {

  const [maara, setMaara] = useState(1);
  const [faktat, setFaktat] = useState([]);

  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(base_url + `jokes/random/${maara}`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {

          setFaktat({ value: json.value });
        });
    };

    lataa_faktat();
  }, [maara]);


  const NumberList = (faktat)=> {
    return (
      <ul>
        {faktat.map((obj, index) =>
          <p  key={index} value={obj.Name} />
        )}
      </ul>
    );
  }


  return (
    <div>
      <input
        value={maara}
        onChange={(e) => setMaara(e.target.value)}
        type="number"
      />
      <button
        onClick={() => setFaktat([])}

      >x</button>
      <button
        onClick={() => setMaara(0)}

      >O</button>

      <div>
        <h3>Faktat</h3>
        <p>Faktoja haettu {maara}</p>
        {console.log(faktat.value)}
        {
          //<p>{JSON.stringify(faktat.value, ["joke", "categories"], 2)}        </p>
        }
        <p>{NumberList}</p>
        
        

      </div>
    </div>

  );
}

export default Search;
