import React, { useEffect, useState } from 'react'

const Search = () => {
  const [data, setData] = useState([])
  const fetchURL = "http://api.icndb.com/"
  const getData = () =>
    fetch(`${fetchURL}jokes/random/1`)
      .then((res) => res.json())

  useEffect(() => {
    getData().then((data) => setData(data))
  }, []);
  const [maara, setMaara] = useState(2);
  const [faktat, setFaktat] = useState([]);

  useEffect(() => {
    const lataa_faktat = () => {
      let base_url = "http://api.icndb.com/"

      fetch(`${fetchURL}jokes/random/1`)
        .then((resp) => {
          return resp.json();
        })
        .then((json) => {
          console.log(json);
          setFaktat(json);
        });
    };

    lataa_faktat();
  }, [maara]);

  return (
    <div>
      <input
        value={data.value}
        onChange={(e) => setData(e.target.value)}
        type="number"
      />
      <button onClick={() => getData()}>Hae</button>

      <div>
        <h3>Faktat</h3>
        <p>Faktoja haettu {faktat.length}</p>
        {data.map((x) => {
          return <p key={x._id}>{x.text}</p>;
        })}
      </div>
    </div>

  );
}

export default Search;
